	 @RequestMapping("saveTempToServer.do")
	 @ResponseBody
	 public Map<String, Object> saveTempToServer(HttpServletRequest req,HttpServletResponse resp,Long moduleItemId){
		Map<String, Object> result = new HashMap<String, Object>();
		try {
			InputStream in = req.getInputStream() ;
			byte[] moduleFile = IOUtils.toByteArray(in);
	        return aimsTemplateDetailService.saveTemplateFile(moduleItemId,moduleFile,null);	 	
		}  	
		catch(Exception e){
			//
			result.put("tag", false);
			result.put("msg", "保存模板文件失败!");
			return result;
		}
	 }
	 
	 /**
	 * <p>函数名称：queryTempName        </p>
	 * <p>功能说明：根据模板文件名称查询模板文件信息，返回模板id+版本号
	 *
	 * </p>
	 *<p>参数说明：</p>
	 * @param req
	 * @param resp
	 * @param ffileName 模板文件名称
	 * @return
	 *
	 * @date   创建时间：2017年2月22日
	 * @author 作者：黄振雄 (mailto:huangzhenxiong@bosssoft.com.cn) 
	 */
	 @RequestMapping("queryTempName.do")
	 @ResponseBody
	 public String queryTempName(HttpServletRequest req,HttpServletResponse resp,String ffileName){
		resp.setContentType("image/gif");// 设置信息内容格式
		resp.setContentType("utf-8") ;
		resp.addHeader("Access-Control-Allow-Origin","*");
		AimsTemplateDetail template=null;
		String tempName ="";
		//如果文件名不为空，则根据文件名称查询
		if(ffileName!=null&&(!"".equals(ffileName))){
			template = aimsTemplateDetailService.queryOneByFileName(ffileName);			
		}
		if(template!=null)
			tempName = template.getModuleItemId()+"_"+String.valueOf(template.getVersion()) ; 
		return tempName;
	 }
