@Controller
@RequestMapping(value="egov/asset/etl/task", name="数据导出任务管理接口")
public class EtlExportTaskController extends BaseController {
	@Autowired
	private AssetEtlExportTaskService expTaskService;
	
	@ResponseBody
	@RequestMapping(value="getEtlExportTaskPage.do", name="获取导出分页数据")
	public GridData getEtlExportTaskPage(Searcher searcher, Page<AssetEtlExportTask> page){
		searcher.addCondition("creator", getCurrUser().getUserCode());
		return new GridData(new PageResult<AssetEtlExportTask>(expTaskService.queryAssetEtlExportTaskPage(searcher, page)));
	}
	
	@ResponseBody
	@RequestMapping(value="deleteEtlExportTask.do", name="删除导出任务")
	public AjaxResult deleteEtlExportTask(AssetEtlExportTask task){
		AjaxResult result = new AjaxResult(AjaxResult.STATUS_SUCCESS, "任务【" + task.getConfigName() + "】删除成功！");
		try{
			expTaskService.delAssetEtlExportTask(task);
		} catch(Exception e){
			result.setStatusCode(AjaxResult.STATUS_ERROR);
			result.setMessage(e.getMessage());
		}
		
		return result;
	}
}
